parameters.parameter:
var lines = value.split('\n');
var data = {};
while(lines.length) {
    var hstr = lines.shift();
    if (!hstr) break;
    var harr = hstr.split(': ');
    var k = harr[0].trim();
    var v = harr[1].trim();
    v = v.split('"').join(''); // remove quotes
    v = v.replace(/.+\(([0-9]+)\)$/, '$1'); // get number 3 from 'slave(3)'
    data[k] = v;
}
return JSON.stringify(data, null, 2);
